mapper.select:
select b.id as id, title, contents, hit, date_format(reg_date, '%Y-%m-%d %h:%i:%s') as regDate, g_no, o_no, depth, user_id as userId, name as userName from board b left outer join user u on b.user_id = u.id
where b.title like #{keyword} or b.contents like #{keyword}
order by g_no desc, o_no asc limit #{offset}, #{pageSize}

mapper.delete:

delete
from board
where id = #{id}
  and user_id = #{userId}

mapper.update:

update board
set title    = #{title},
    contents = #{contents}
where id = #{id}

update board
set hit = hit + 1
where id = #{id}

update board
set o_no = o_no + 1
where g_no = #{g_no}
  and o_no >= #{o_no}
